import React from "react";
import { Link } from "react-router-dom";

export default function BlogCard({ blog }) {
  return (
    <div className="card">
      {blog.image && <img src={blog.image} alt={blog.title} className="card-img" />}
      <div className="card-body">
        <h3>{blog.title}</h3>
        <p dangerouslySetInnerHTML={{ __html: blog.excerpt || (blog.content.slice(0, 200) + "...") }} />
        <div className="card-meta">
          <small>By {blog.author?.name || "Unknown"}</small>
          <Link to={`/blogs/${blog._id}`}>Read</Link>
        </div>
      </div>
    </div>
  );
}
