import React, { useEffect, useState } from "react";
import { fetchBlogs } from "../services/blogService";
import BlogCard from "../components/BlogCard";
import { Link } from "react-router-dom";

export default function Home() {
  const [blogs, setBlogs] = useState([]);
  const [q, setQ] = useState("");

  useEffect(() => {
    load();
  }, []);

  const load = async () => {
    const data = await fetchBlogs();
    setBlogs(data);
  };

  const filtered = blogs.filter(b => b.title.toLowerCase().includes(q.toLowerCase()) || (b.tags || []).join(" ").includes(q.toLowerCase()));

  return (
    <div className="container">
      <div className="home-head">
        <h1>Latest Posts</h1>
        <div>
          <input placeholder="Search by keyword or tag" value={q} onChange={(e) => setQ(e.target.value)} />
          <Link to="/blogs/create" className="btn">Write</Link>
        </div>
      </div>
      <div className="grid">
        {filtered.map(blog => <BlogCard key={blog._id} blog={blog} />)}
      </div>
    </div>
  );
}
