import React, { useEffect, useState } from "react";
import { fetchBlogs } from "../services/blogService";
import BlogCard from "../components/BlogCard";
import { Link } from "react-router-dom";

export default function Home() {
  const [blogs, setBlogs] = useState([]);
  const [q, setQ] = useState("");

  useEffect(() => {
    load();
  }, []);

  const load = async () => {
    const data = await fetchBlogs();
    setBlogs(data);
  };

  const filtered = blogs.filter(b => b.title.toLowerCase().includes(q.toLowerCase()) || (b.tags || []).join(" ").includes(q.toLowerCase()));

  return (
    <div className="container">
      <div className="home-head">
        <h1>Latest Posts</h1>
        <div className="search-write">
          <div className="search-container">
            <input 
              placeholder="Search by keyword or tag" 
              value={q} 
              onChange={(e) => setQ(e.target.value)} 
            />
          </div>
          <Link to="/blogs/create" className="btn">
            <span>Write a Post</span>
          </Link>
        </div>
      </div>
      <div className="grid">
        {filtered.length > 0 ? (
          filtered.map(blog => <BlogCard key={blog._id} blog={blog} />)
        ) : (
          <div className="no-results">
            <h3>No posts found</h3>
            <p>Try a different search term or create a new post.</p>
          </div>
        )}
      </div>
    </div>
  );
}
